import io from "socket.io-client";
import axios from "axios";
import { useEffect, useRef, useState } from "react";
import Cookies from "js-cookie";
import MainPageLoading from "./MainPageLoading";
import MainPageConnected from "./MainPageConnected";
import MainPageDefault from "./MainPageDefault";
import MainPageDisconnected from "./MainPageDisconnected";

const socket = io("https://192.168.0.114:5000");
const configuration = {
  iceServers: [{ urls: "stun:stun.l.google.com:19302" }],
};

export default function MainPage() {
  const [localStream, setLocalStream] = useState<MediaStream | undefined>();
  const [infoSocket, setInfoSocket] = useState<any>(null);
  const [peerConnection, setPeerConnection] = useState<RTCPeerConnection | null>(null);
  const [isLoading, setLoading] = useState<boolean>(false);
  const [isDisconnected, setDisconnected] = useState<boolean>(false);
  const [mediaRecorder, setMediaRecorder] = useState<MediaRecorder | undefined>(undefined);
  const [recordedChunks, setRecordedChunks] = useState<BlobPart[]>([]);

  const localStreamRef = useRef<MediaStream | undefined>(undefined);
  const infoSocketRef = useRef<any>(null);
  const loadingRef = useRef<boolean>();
  const remoteAudioRef = useRef<HTMLAudioElement>(null);

  const initializePeerConnection = () => {
    const newPeerConnection = new RTCPeerConnection(configuration);
    newPeerConnection.ontrack = (event) => {
      if (event.streams && event.streams[0]) {
        console.log('123');
        remoteAudioRef.current!.srcObject = event.streams[0];
      }
    };
    newPeerConnection.onicecandidate = (event) => {
      if (event.candidate) {
        socket.emit("candidate", {
          candidate: event.candidate,
          target: infoSocketRef.current?.connectedUserSocketID,
        });
      }
    };
    setPeerConnection(newPeerConnection);
    return newPeerConnection;
  };

  useEffect(() => {
    const currentPeerConnection = initializePeerConnection();

    socket.on("connection", (data) => {
      setLoading(false);
      loadingRef.current = false;
      setInfoSocket((prevState:any) => {
        return {
          ...prevState,
          connectedUserID: data.userID,
          connectedUserSocketID: data.socketID,
          isSendingOffer: data.isSendingOffer,
        };
      });
      infoSocketRef.current = {
        ...infoSocketRef.current,
        connectedUserID: data.userID,
        connectedUserSocketID: data.socketID,
        isSendingOffer: data.isSendingOffer,
      };
    });

    socket.on("candidate", (data) => {
      currentPeerConnection
        .addIceCandidate(data.candidate)
        .then(() => {
          console.log("added Candidate", data.candidate, "from ", data.sender);
        })
        .catch((error) => console.log("Ошибка установки ICE кандидата", error));
    });

    socket.on("answer", async (data) => {
      const answer = new RTCSessionDescription({
        type: data.type,
        sdp: data.sdp,
      });

      try {
        await currentPeerConnection.setRemoteDescription(answer);
        console.log("Удаленное описание ответа установлено успешно.");
      } catch (error) {
        setLoading(false);
        loadingRef.current = false;
        console.error("Ошибка при установке удаленного описания ответа:", error);
      }
    });

    socket.on("offer", async (data) => {
      const offer = new RTCSessionDescription({
        type: data.type,
        sdp: data.sdp,
      });

      try {
        await currentPeerConnection.setRemoteDescription(offer);
        const answer = await currentPeerConnection.createAnswer();
        await currentPeerConnection.setLocalDescription(answer);
        console.log(`emit answer to ${infoSocketRef.current}`);
        socket.emit("answer", {
          type: "answer",
          sdp: currentPeerConnection.localDescription?.sdp,
          infoSocket: infoSocketRef.current,
        });
      } catch (error) {
        setLoading(false);
        loadingRef.current = false;
        console.error("Ошибка при установке удаленного описания предложения:", error);
      }
    });

    socket.on("endCall", () => {
      console.log("disconnect");
      setInfoSocket(null);
      infoSocketRef.current = null;
      setLocalStream(undefined);
      localStreamRef.current = undefined;
      setDisconnected(true);
      mediaRecorder?.stop();
    });

    return () => {
      localStream?.getTracks().forEach((track) => track.stop());
      mediaRecorder?.stop();
      socket.off("connection");
      socket.off("offer");
      socket.off("answer");
      socket.off("candidate");
      socket.off("endCall");
    };
  }, []);

  const getUserId = () => {
    const id = Cookies.get("userIdState");
    if (id === undefined) {
      alert("Сначала войдите в аккаунт");
      return;
    }
    setLoading(true);
    loadingRef.current = true;
    socket.emit("auth", id);
    setInfoSocket((prevState:any) => {
      return { ...prevState, socketID: socket.id, userID: id };
    });
    infoSocketRef.current = {
      ...infoSocketRef.current,
      socketID: socket.id,
      userID: id,
    };
    getMedia(id);
  };

  const getMedia = async (id: string) => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        audio: true,
        video: false,
      });
      console.log("stream");
      setLocalStream(stream);
    } catch (err) {
      setLoading(false);
      loadingRef.current = false;
      console.error("Не удалось получить доступ к микрофону", err);
    }
  };

  useEffect(() => {
    if (infoSocket?.userID) {
      getMedia(infoSocket.userID);
    }
  }, [infoSocket?.userID]);

  useEffect(() => {
    if (localStream && !localStreamRef.current) {
      startCall(localStream);
      localStreamRef.current = localStream;
    }
  }, [localStream]);

  useEffect(() => {
    if (infoSocket?.isSendingOffer) {
      createOffer();
    }
  }, [infoSocket?.connectedUserID]);

  const startCall = async (stream: MediaStream) => {
    try {
      const offer = await peerConnection!.createOffer();
      await peerConnection!.setLocalDescription(offer);
      socket.emit("searchForAUser", infoSocket?.userID);

      stream.getTracks().forEach((track) => {
        console.log("track");
        peerConnection!.addTrack(track, stream);
      });
    } catch (error) {
      console.error("Ошибка создания или установки предложения.", error);
      setLoading(false);
      loadingRef.current = false;
    }
  };

  const createOffer = async () => {
    const offer = await peerConnection!.createOffer();
    peerConnection!.setLocalDescription(offer).then(() => {
      socket.emit("offer", {
        type: "offer",
        sdp: peerConnection!.localDescription?.sdp,
        infoSocket: infoSocketRef.current,
      });
    });
  };

  const cancelLoading = () => {
    setLoading(false);
    loadingRef.current = false;
    socket.emit("endCall", infoSocketRef.current);
  };

  const terminateCall = () => {
    if (localStream) {
      localStream.getTracks().forEach((track) => {
        track.stop();
      });
      setLocalStream(undefined);
      localStreamRef.current = undefined;
    }

    if (peerConnection) {
      peerConnection.close();
      const newPeerConnection = initializePeerConnection();
      setPeerConnection(newPeerConnection);
    }

    setLoading(false);
    loadingRef.current = false;
    socket.emit("endCall", infoSocketRef.current);
    setDisconnected(true);
    setInfoSocket(null);
    infoSocketRef.current = null;
  };

  return (
    <>
      {isDisconnected ? (
        <MainPageDisconnected returnBack={setDisconnected} />
      ) : loadingRef.current ? (
        <MainPageLoading cancelLoading={cancelLoading} />
      ) : infoSocket?.connectedUserID ? (
        <MainPageConnected
          localStream={localStream}
          remoteAudioRef={remoteAudioRef}
          terminateCall={terminateCall}
        />
      ) : (
        <MainPageDefault
          getUserId={getUserId}
          localStream={localStream}
          remoteAudioRef={remoteAudioRef}
        />
      )}
    </>
  );
}
