import io from "socket.io-client";
import { useEffect, useState } from "react";
import Cookies from "js-cookie";

const socket = io("http://localhost:5000");
const configuration = {
  iceServers: [{ urls: "stun:stun.l.google.com:19302" }],
};

export default function MainPage() {
  const [localStream, setLocalStream] = useState<MediaStream>();

  useEffect(() => {
    // Возвращаем функцию очистки
    return () => {
      localStream?.getTracks().forEach((track) => track.stop());
      socket.off("offer");
      socket.off("answer");
      socket.off("candidate");
    };
  }, []);

  const getUserId = () => {
    const id = Cookies.get("userIdState");
    console.log(socket.id)
    if (id === undefined) {
      alert("Сначала войдите в аккаунт");
      return;
    }
    socket.emit("auth", id);
    getMedia(id);
  };


  // Запрашиваем доступ к микрофону при монтировании компонента
  const getMedia = async (id: string) => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      setLocalStream(stream);
      startCall(stream, id);
    } catch (err) {
      console.error("Не удалось получить доступ к микрофону", err);
    }
  };

  // Функция для старта WebRTC соединения
  const startCall = (stream?: MediaStream, id?: string) => {
    const peerConnection = new RTCPeerConnection(configuration);
    // Обработчик события генерации ICE кандидатов
    peerConnection!.onicecandidate = (event) => {
      if (event.candidate) {
        socket.emit("candidate", event.candidate);
      }
    };

    // Добавляем треки в peerConnection
    stream?.getTracks().forEach((track) => {
      peerConnection?.addTrack(track, stream);
    });

    // Создаём предложение соединения
    peerConnection
      ?.createOffer()
      .then((offer) => {
        return peerConnection.setLocalDescription(offer);
      })
      .then(() => {
        // Отправляем предложение другому пиру через сигнализационный сервер
        socket.emit("offer", {
          target: id,
          sdp: peerConnection.localDescription,
        });
      })
      .catch((err) => console.error(err));

    socket.on("offer", (data) => {
      if (data.sdp) {
        peerConnection?.setRemoteDescription(
          new RTCSessionDescription(data.sdp)
        );
        // Отправляем ответ на предложение другому пиру
        peerConnection
          ?.createAnswer()
          .then((answer) => {
            return peerConnection.setLocalDescription(answer);
          })
          .then(() => {
            console.log(peerConnection.localDescription)
            socket.emit("answer", {
              target: data.sender,
              sdp: peerConnection.localDescription,
            });
          })
          .catch((err) => console.error(err));
      }
    });

    // При получении ответа от пользователя
    socket.on("answer", (answer) => {
      alert('покдлючено к')
      console.log(answer)
      peerConnection?.setRemoteDescription(new RTCSessionDescription(answer.sdp));
    });

    // При получении ICE кандидата от другого пользователя
    socket.on("candidate", (candidate) => {
      peerConnection?.addIceCandidate(new RTCIceCandidate(candidate));
    });
  };

  return (
    <>
      <section className="w-full h-full flex content-center items-center flex-col bg-cyan-500 mx-[500px] shadow-2xl shadow-black">
        <div className="flex flex-grow content-center items-center min-h-[200px] min-w-[200px] max-h-[200px] max-w-[200px]  mt-10">
          <div className="w-full h-full flex content-center  bg-anon bg-cover bg-no-repeat rounded-3xl "></div>
        </div>
        <div className="flex pb-10 hover:transition-all hover:opacity-80 flex-col items-center justify-center">
          <button
            onClick={() => {
              getUserId();
            }}
            className="bg-green-300 rounded-3xl text-3xl w-[250px] h-[40px] mb-5 mt-80 text-white"
          >
            Начать поиск
          </button>
          <p className="text-1xl">Количество пользователей онлайн:</p>
        </div>
      </section>
    </>
  );
}
