import { configureStore } from "@reduxjs/toolkit";
import { useReducer } from "react";
import { applyMiddleware, combineReducers } from "redux";

const rootReducer = combineReducers({
    user:useReducer
})

export const store = configureStore({
    reducer:rootReducer,
})
