import { FC, useRef, useState, useEffect } from "react";
import { LoginProps } from "./LoginPage";
import LoginAsk from "./LoginAsk";
import Cookies from "js-cookie";
import LoginSuccess from "./LoginSuccess";

const LoginPage: FC<LoginProps> = ({
  isActive,
  setActive,
}) => {

  const [savedState, setSavedState] = useState<string | undefined>(undefined)

  const savedStateRef = useRef<string | undefined>(savedState)

  useEffect(() => {
    if(Cookies.get('loginState') != undefined){
      setSavedState(JSON.parse(Cookies.get('loginState') as string))

    }
    savedStateRef.current = savedState
  }, [savedState])

  return (
    <>
      <section className="w-full flex h-full flex-col items-center content-center pt-10 text-4xl mx-[500px] bg-cyan-500">
        {savedState ? (
          <LoginSuccess nickName={savedState} />
        ) : (
          <LoginAsk
            isActive={isActive}
            setActive={setActive}
          />
        )}
      </section>
    </>
  );
};

export default LoginPage;
