import { FC, useRef, useState } from "react";
import { LoginProps } from "./LoginPage";
import LoginAsk from "./LoginAsk";
import Cookies from "js-cookie";
import LoginSuccess from "./LoginSuccess";

const LoginPage: FC<LoginProps> = ({
  isActive,
  setActive,
}) => {

  const [savedState, setSavedState] = useState(Cookies.get('loginState'))
  const savedStateRef = useRef(savedState)

  return (
    <>
      <section className="w-full flex h-full flex-col items-center content-center pt-10 text-4xl mx-[500px] bg-cyan-500">
        {savedStateRef.current ? (
          <LoginSuccess nickName={savedStateRef.current} />
        ) : (
          <LoginAsk
            isActive={isActive}
            setActive={setActive}
          />
        )}
      </section>
    </>
  );
};

export default LoginPage;
