import { FC, useEffect, useState } from "react";
import { getRecordings } from "../../actions/audio";
import Cookies from "js-cookie";
const UserRecordings: FC = () => {
  const [filesArray, setFilesArray] = useState([]);

  useEffect(() => {
    console.log("getZ");
    if (Cookies.get("userIdState")) {
      getRecordings(Cookies.get("userIdState"), setFilesArray);
    }
  }, []);

  return (
    <>
        {
        filesArray.length > 0 ? (
          <section className="flex flex-wrap flex-col text-white content-center gap-8 w-full">
            {filesArray.map((recording:any) => (
              <div className="grid grid-flow-col w-full h-[100px] items-center rounded-lg bg-cyan-500 justify-between gap-56">
                <a
                  href="#"
                  className="h-[80px] w-[80px] ml-5 bg-white justify-self-start"
                ></a>
                <h2 className="text-black text-2xl self-center">{recording.filename}</h2>
                <h4 className="text-gray-600 text-xl mr-2">31:45</h4>
              </div>
            ))}
          </section>
        ) : (
          <section className="flex flex-wrap flex-col text-white content-center gap-8 w-full">
          <div className="grid grid-flow-col w-full h-[100px] items-center rounded-lg bg-cyan-500 justify-between gap-56">
            <h2 className="text-black text-2xl">Записей нет</h2>
          </div>
          </section>
        )
      }
    </>
  );
};

export default UserRecordings;
