import { FC, useEffect, useState } from "react";
import { LoginProps } from "./LoginPage";
import LoginAsk from "./LoginAsk";
import Cookies from "js-cookie";
import LoginSuccess from "./LoginSuccess";

const LoginPage: FC<LoginProps> = ({
  isActive,
  setActive,
  callback,
  setNickNameHandler,
}) => {
  const [nickName, setNickName] = useState<string>("");
  const [isLogged, setIsLogged] = useState<boolean>(
    Cookies.get("loginState") ? true : false
  );

  useEffect(() => {
    const savedState = Cookies.get("loginState");
    if (savedState) {
      setNickName(JSON.parse(savedState));
    }
  }, []);

  const exitFromAccount = (arg: string): void => {
    setNickNameHandler(arg);
    setIsLogged(false);
  };

  return (
    <>
      <section className="w-full flex h-full flex-col items-center content-center pt-10 text-4xl mx-[500px] bg-cyan-500">
        {Cookies?.get("loginState") == "" ? (
          <LoginAsk
            isActive={isActive}
            setActive={setActive}
            callback={callback}
            setNickNameHandler={setNickNameHandler}
          />
        ) : (
          <LoginSuccess nickName={nickName} setNickName={exitFromAccount} />
        )}
      </section>
    </>
  );
};

export default LoginPage;
